import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Admin.css';
import { useNavigate } from 'react-router-dom';

function Admin() {
    const navigate = useNavigate();
    const [tables, setTables] = useState([]);
    const [selectedTable, setSelectedTable] = useState(null);
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8084/api/admin/tables')
            .then(response => setTables(response.data))
            .catch(error => console.error('Error fetching tables', error));
    }, []);

    const fetchOrders = (tableId) => {
        axios.get(`http://localhost:8084/api/order/getOrders/${tableId}`)
            .then(response => {
                setOrders(response.data);
            })
            .catch(error => console.error('Error fetching orders', error));
    };

    const handleTableClick = (tableId) => {
        setSelectedTable(tableId);
        fetchOrders(tableId);
    };

    const handleCheckout = async (tableId) => {
        if (window.confirm('Are you sure you want to checkout and clear all orders for this table?')) {
            try {
                // Assuming the checkout call should actually use DELETE method
                const response = await axios.delete(`http://localhost:8084/api/receipt/${tableId}/checkout`);
                const receipt = response.data;
                navigate(`/receipt/${tableId}`, { state: { receipt } });
            } catch (error) {
                console.error('Error checking out', error);
            }
        }
    };

    return (
        <div>
            <h2>Admin Dashboard</h2>
            <div className="tables-grid">
                {tables.map(table => (
                    <button key={table} onClick={() => handleTableClick(table)}>Table ID: {table}</button>
                ))}
            </div>
            <div className="orders-container" style={{ maxHeight: '70%', overflowY: 'scroll' }}>
                {selectedTable && orders.length > 0 ? (
                    orders.map(order => (
                        <div key={order.id}>
                            <p>Order ID: {order.id}</p>
                            <ul>
                                {order.orderItems.map(item => (
                                    <li key={item.id}>{item.menuItemName} x {item.quantity}</li>
                                ))}
                            </ul>
                            <button onClick={() => handleCheckout(selectedTable)}>Checkout</button>
                        </div>
                    ))
                ) : (
                    <p>No orders</p>
                )}
            </div>
        </div>
    );
}

export default Admin;
