import React, { useEffect } from 'react';
import './AboutSection.css';

const AboutSection = () => {
  useEffect(() => {
    const bg = document.querySelector('.about-container::before'); // Won't work as CSS pseudo-elements cannot be accessed directly via JS DOM API

    const handleScroll = () => {
      const scrollY = window.scrollY;
      const blurValue = Math.min(5, 2 + 0.1 * scrollY / 20); // Calculate blur value
      // Need to directly modify style via container since pseudo-elements can't be accessed directly
      document.querySelector('.about-container').style.backdropFilter = `blur(${blurValue}px)`;
    };

    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className="about-container">
      <div className="about-content">
        <h1>Welcome to Our Café!</h1>
        <p>Located in the heart of the city, we serve freshly brewed coffee and a selection of artisan pastries. Enjoy a warm, inviting atmosphere with vintage décor that takes you back in time.</p>
        <a href="#menu" className="btn">Explore Our Menu</a>
      </div>
    </section>
  );
}

export default AboutSection;
