import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTableContext } from '../context/TableContext';
import './Cart.css';

const Cart = ({ onClose }) => {
    const [cartItems, setCartItems] = useState([]);
    const { tableNo } = useTableContext();

    useEffect(() => {
        fetchCartItems();
    }, [tableNo]);

    const fetchCartItems = () => {
        axios.get(`http://localhost:8084/api/${tableNo}/getCart`)
            .then(response => setCartItems(response.data))
            .catch(error => console.error('Error fetching cart items:', error));
    };

    const handleRemoveFromCart = (menuItemId) => {
        axios.post(`http://localhost:8084/api/${tableNo}/removeFromCart/${menuItemId}`)
            .then(() => fetchCartItems())
            .catch(error => console.error('Error removing item from cart:', error));
    };

    const handlePlaceOrder = () => {
        axios.post(`http://localhost:8084/api/${tableNo}/placeOrder`)
            .then(response => {
                alert('Order placed successfully!');
                onClose(); // Close the cart after placing the order
                fetchCartItems(); // Refresh the cart, assuming it should be empty now
            })
            .catch(error => {
                console.error('Error placing order:', error);
                alert('Failed to place order.');
            });
    };

    return (
        <div className="cart-container">
            <button onClick={onClose} className="close-button">X</button>
            {cartItems.map(item => (
                <div key={item.id}>
                    <p>{item.name} - {item.quantity}</p>
                    <button onClick={() => handleRemoveFromCart(item.menuItemId)}>Remove</button>
                </div>
            ))}
            <div className="place-order-button-container">
                <button onClick={handlePlaceOrder} className="place-order-btn">Place Order</button>
            </div>
        </div>
    );
};

export default Cart;
