import React, { createContext, useState, useContext, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const TableContext = createContext();

export const useTableContext = () => useContext(TableContext);

export const TableProvider = ({ children }) => {
    const [tableNo, setTableNo] = useState('');
    const navigate = useNavigate();
    const location = useLocation();
    
    useEffect(() => {
        // Extract tableNo from URL path assuming it comes after the first slash
        const tableNoFromPath = location.pathname.split('/')[1]; // path should look like '/T1'
        if (tableNoFromPath && tableNoFromPath !== tableNo) {
            setTableNo(tableNoFromPath);
        } else if (!tableNoFromPath) {
            // If path does not contain tableNo, navigate to a default
            navigate('/T2'); // default to T2 if no tableNo specified
        }
    }, [location, navigate, tableNo]);
    
    return (
        <TableContext.Provider value={{ tableNo, setTableNo }}>
            {children}
        </TableContext.Provider>
    );
};
