import React, { useState, useEffect } from 'react';
import './Admin.css'

const Admin = () => {
    const [orders, setOrders] = useState([]);
    const [filter, setFilter] = useState('');

    useEffect(() => {
        fetch("http://localhost:8084/api/order/getAllOrders")
            .then(res => res.json())
            .then(data => setOrders(data))
            .catch(err => console.error("Error fetching orders:", err));
    }, []);

    const handleServed = (orderId) => {
        fetch(`http://localhost:8084/api/order/${orderId}/isDone`, {
            method: 'POST', // or PUT, depending on API
        })
            .then(response => response.json())
            .then(data => {
                console.log('Success:', data);
                setOrders(orders => orders.filter(order => order.id !== orderId));
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    const filteredOrders = filter ? orders.filter(order => order.tableId === filter) : orders;

    return (
        <div>
            <input
                className="filter-input"
                type="text"
                value={filter}
                onChange={e => setFilter(e.target.value)}
                placeholder="Filter by table ID (e.g., T2)"
            />
            <div className="admin-dashboard">

                {filteredOrders.map(order => (
                    <div key={order.id} className="order-card">
                        <h2>Table {order.tableId}- {order.id}</h2>
                        <ul>
                            {order.orderItems.map(item => (
                                <li key={item.id}>{item.menuItemName} x {item.quantity}</li>
                            ))}
                        </ul>
                        <button className="served-button" onClick={() => handleServed(order.id)}>
                            âœ” Served
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Admin;
