import React from 'react';
import { useLocation } from 'react-router-dom';

function Receipt() {
    const location = useLocation();
    const { receipt } = location.state;

    return (
        <div>
            <h2>Receipt</h2>
            <h3>Receipt ID: {receipt.id}</h3>
            <h3>Items Total: {receipt.itemsTotal}</h3>
            <h3>Tax: {receipt.tax}</h3>
            <h3>Grand Total: {receipt.grandTotal}</h3>
            {receipt.receiptItemDTOList.map(item => (
                <p key={item.id}>{item.menuItemName} x{item.quantity}</p>
            ))}
        </div>
    );
}

export default Receipt;
