// src/components/Navbar.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
const Navbar = () => {
    const [categories, setCategories] = useState([]);
  
    useEffect(() => {
      axios.get(`http://localhost:8084/api/categories`) // Adjust the URL based on your API
        .then(response => {
          setCategories(response.data);
        })
        .catch(error => console.error('Error fetching categories:', error));
    }, []);
  
    const scrollToCategory = (categoryName) => {
      const categoryElement = document.getElementById(categoryName);
      if (categoryElement) {
        const offset = 50; // Height of the fixed navbar
        const bodyRect = document.body.getBoundingClientRect().top;
        const elementRect = categoryElement.getBoundingClientRect().top;
        const elementPosition = elementRect - bodyRect;
        const offsetPosition = elementPosition - offset;
  
        window.scrollTo({
          top: offsetPosition,
          behavior: 'smooth'
        });
      }
    };
  
    return (
      <nav>
        <h1>Cafe Name</h1>
        <ul>
          {categories.map(category => (
            <li key={category.id} onClick={() => scrollToCategory(category.name)}>
              {category.name}
            </li>
          ))}
        </ul>
      </nav>
    );
  };
  
  export default Navbar;
