import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTableContext } from '../context/TableContext';

const Navbar = () => {
    const [categories, setCategories] = useState([]);
    const { tableNo, setTableNo } = useTableContext();

    useEffect(() => {
      axios.get(`http://localhost:8084/api/categories`) // Adjust the URL based on your API
        .then(response => {
          setCategories(response.data);
        })
        .catch(error => console.error('Error fetching categories:', error));
    }, [tableNo]);

    const scrollToTop = () => {
      window.scrollTo({
        top: 0,
        behavior: 'smooth'  // This enables smooth scrolling
      });
    };

    const scrollToCategory = (categoryName) => {
      const categoryElement = document.getElementById(categoryName);
      const navbarHeight = document.querySelector('nav').offsetHeight;
  
      if (categoryElement) {
        const elementPosition = categoryElement.offsetTop;
        const offsetPosition = elementPosition - navbarHeight - 10; // Adding 10px padding
  
        window.scrollTo({
          top: offsetPosition,
          behavior: 'smooth'
        });
      }
    };

    return (
      <nav>
        <h1 onClick={scrollToTop}>Mocha Muse</h1>
        <ul>
          {categories.map(category => (
            <li key={category.id} onClick={() => scrollToCategory(category.name)}>
              {category.name}
            </li>
          ))}
        </ul>
      </nav>
    );
  };

export default Navbar;
