import React from 'react';
import { useLocation } from 'react-router-dom';
function Receipt() {
    const location = useLocation();
    const { receipt } = location.state;

    // Optionally add loading state and error handling here

    return (
        <div>
            <h2>Receipt Details</h2>
            {receipt ? (
                <>
                    <h3>Receipt ID: {receipt.id}</h3>
                    <h3>Items Total: ${receipt.itemsTotal.toFixed(2)}</h3>
                    <h3>Tax: ${receipt.tax.toFixed(2)}</h3>
                    <h3>Grand Total: ${receipt.grandTotal.toFixed(2)}</h3>
                    <ul>
                        {receipt.receiptItemDTOList.map(item => (
                            <li key={item.id}>{item.menuItemName} x {item.quantity}</li>
                        ))}
                    </ul>
                </>
            ) : (
                <p>Loading receipt details...</p>
            )}
        </div>
    );
}

export default Receipt;
